import javax.swing.JOptionPane;

public class Calisma1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		
		
		for (int a=0 ; a<=10;a++)
		{
			if(a%2==0)
			{	
				System.out.println(a+" ->" + a*a);
		}
			else {
				System.out.println(a+ "->" + a*a*a);
			}
		
		}
		
		
		
	}

}
